mapper.insert:
INSERT INTO cart (
		userid,
		productid,
		qty
) VALUES (
		#{userId},
		#{productId},
		#{amount}
)

mapper.update:
UPDATE cart
SET qty = #{amount}
WEHRE cartid = #{cartId}

mapper.select:
SELECT count(cartid) as result
FROM sellremarket.cart
WHERE cartid = #{cartId}
	AND userid = #{userId}
